int udp_setup(char *host)
{
    struct sockaddr_in si_me, si_other;
    int s, i, slen=sizeof(si_other);
    char buf[BUFLEN];
    char message[BUFLEN];

    if((s=socket(AF_INET, SOCK_DGRAM, IPPROTO_UDP)) == -1)
    {
        die("socket");
    }

    memset((char*) &si_other, 0, sizeof(si_other));
    memset((char*) &si_me, 0, sizeof(si_me));

    si_other.sin_family = AF_INET;
    si_other.sin_port = htons(PORT);

    si_me.sin_family = AF_INET;
    si_me.sin_addr.s_addr = htonl(INADDR_ANY);
    si_me.sin_port = htons(0);

    if(inet_aton(host, &si_other.sin_addr) == 0) {
        die("inet_aton");
    }

    if(bind(s, (struct sockaddr *) &si_me, sizeof(si_me)) < 0) {
        die("bind()");
    }

    char *handshake = "handshake";

    if(sendto(s, handshake, strlen(handshake), 0, (struct sockaddr *) &si_other, slen) == -1)
    {
        die("sendto()");
    }

    if(recvfrom(s, buf, BUFLEN, 0, (struct sockaddr *) &si_other, &slen) == -1)
    {
        die("recvfrom()");
    }

    // Expecting buffer with the format "xxx.xxx.xxx.xxx:PORT"
    char *resp;
    resp = strtok(buf, ":");

    IP = strtok(NULL, ":");
    Port = ntohs(si_me.sin_port);

    return s;
}

